object Anagram {
  def anagram(s: String): Int = {
    // count the number of each char in each half
    // for each char, subtract the # of each in in left from # in right
    // add up the differences that are > 0

    val l = s.length
    if (l % 2 == 1) -1
    else {
      val alphabet = "abcdefghijklmnopqrstuvwxyz"
      val counts = Array.fill(alphabet.length)(0)
      var alphamap = (alphabet zip counts).toMap

      val left = s.take(l / 2)
      val right = s.takeRight(l / 2)

      for (l <- left) {
        alphamap = alphamap + (l -> (alphamap(l) + 1))
      }
      for (r <- right) {
        alphamap = alphamap + (r -> (alphamap(r) - 1))
      }
      alphamap.filter(p => p._2 > 0).values.sum
    }
  }

  def main(args: Array[String]): Unit = {
